$(document).ready(function(){$(".social-icon").on("click",function(){var c=$(this);var a=c.data("platform");var d=c.data("message");var b=c.data("url");if(a=="mail"){return true}else{var f,e;if(a=="twitter"){f="http://twitter.com/home?status="+encodeURI(d)+"+"+b}else{if(a=="facebook"){f="http://www.facebook.com/share.php?u"+b+"&amp;title="+encodeURI(d)}}e=window.open(f,"name","height=500,width=600");if(window.focus){e.focus()}return false}});$(".Post-content").find("img").each(function(){$(this).parent().addClass("Post-image")})});